package com.syntax.class02;

public class ExamplesArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=900;
		int b=100;
		int c=10;
		int d=3;
		int e=10;
		int f=5;
		int sum=c+d;
		int sub=c-d;
		int remainder=c%d;
		int div=c/d;
		 System.out.println("Sum is =" + sum);
		 System.out.println("Sub is =" + sub);
		 System.out.println("Reminder is =" + remainder);
		 System.out.println("Division of integers = " + div);
		 
		 double num1=10.0;
		 double num2=3.0;
		 
		 System.out.println("Division of double = " +num1/num2);
		 System.out.println(num1%num2);
		 
		 System.out.println(" using float data type-----");
		 
		 float number1=10.0f;
		 float number2=3.0f;
		  System.out.println("Division of float =" + number1/number2);
		  System.out.println(number1%number2);
		  System.out.println(10+10/10);
		  System.out.println((10+10)/10);

	}

}
