package com.syntax.class02;

public class HomeWork {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//First assingment
		double apple = 1.99;
		double orange = 2.99;
		
		double addition = apple + orange;
		double subtraction = apple - orange;
		double multiplication = apple * orange;
		double division = apple / orange;
		
		System.out.println("The addition of 2 numbers " + apple + " and " + orange + " is equal to " + addition);
		System.out.println("The subtraction of 2 numbers " + apple + " and " + orange + " is equal to " + subtraction);
		System.out.println("The multiplication of 2 numbers " + apple + " and " + orange + " is equal to " + multiplication);
		System.out.println("The division of 2 numbers " + apple + " and " + orange +" is equal to " + division);
		
		//Second assignment
		double number = 3.9;
		double square = number * number;
		
		System.out.println("The square of the " + number + " is " + square);
		
		//Third assignment
		int width = 5;
		int height = 8;
		
		double area = width * height;
		double parimeter = 2 * (width + height);
		
		System.out.println("The parimeter of a rectangle width and hight is equal to " + parimeter + " and the area is " + area );
		
	}

}
