package com.syntax.class02;

public class JavaKeywords {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Rules # for identifiers =name "variables, methods
		//#1. cannot use keywords as identifiers
		//ex: String new="Hello";
		
		//#2. cannot have spaces in identifiers
		//ex: String last name="Smith";
		
		//#3.identifiers cannot start with numbers
		//ex: int 1number=123; -> number can be written after any word
		
		/* #4.identifiers cannot contain any special characters except "$" and "_"
		 * ex: boolean hello!=true; not allowed
		 * ex: char #letter='A'; not allowed
		 */
		//Ex. of allowed ones.
		double $price=9.98; 
		float _price1=1.99F;
		
		/*
		 * 
		 * Naming COnventions:
		 * 
		 * Class should start with Upper case and followed camel case
		 * 
		 * variables should start with Lower case letter and followed camel case
		 */
		
		String Weather="hot";
		
		String myWeather="cold"; //->camel case
		
		

	}

}
